mapper.insert:
      INSERT INTO event.event
      (
            id
            , name
            , account_id
            , date
            , head_count
            , explanation
            , application_start_date
            , application_end_date
            , create_date
            , update_date
       )
       VALUES
       (
            #{event.id}
            , #{event.name}
            , #{accountId}
            , #{event.eventDetail.period.dateOf.timeStamp.date}
            , #{event.eventDetail.content.headCount.intHeadCount}
            , #{event.eventDetail.content.explanation.explanation}
            , #{event.eventDetail.period.applicantDateRange.start.timeStamp.date}
            , #{event.eventDetail.period.applicantDateRange.end.timeStamp.date}
            , current_timestamp
            , current_timestamp
         );
